            "formdata": values,
            "job_applicant": job_applicant
        }
    })
    .success(success => {
        console.log(typeof(success.message))
        msgprint("Interview has been scheduled....");
    })
}
function isEmpty(obj) {
    for(var key in obj) {
        if(obj.hasOwnProperty(key))
            return false;
    }
    return true;
}
